void readClasses(String path,std::vector<string> &classes);
int main(int argc, char *argv[]){

std::string path;
  std::vector<string> classes; if(argc==1){
         path = argv[0];
         readClasses(path,&classes);
    }


    for(int i = 0;i<classes.size();i++)
    {
        std::cout<<classes[i] <<"\n"<<;
    }


